
public class principal {
	public static void main(String[]args) {
		//TODO Auto-generated method stub
		
		
		
		//Feito da minha forma:
		int soma = 85+96;
		int subtracao = 100-50;
		int divisao = 40/8;
		int multiplicacao = 7*6;
		
		
		System.out.println("A soma dos números 85 e 96 é: " + soma);
		System.out.println("A subtração dos números 100 e 50 é: " + subtracao);
		System.out.println("A divisão dos números 40 e 8 é: " + divisao);
		System.out.println("A multiplicação dos números 7 e 6 é: " + multiplicacao);
		
		
		
		
		//Feito pelo professor:
		System.out.println("A soma dos números 85 e 96 é: " + (85+96) );
		System.out.println("A subtração dos números 100 e 50 é: " + (100-50) );
		System.out.println("A divisão dos números 40 e 8 é: " + (40/8) );
		System.out.println("A multiplicação dos números 7 e 6 é: " + (7*6) );
		
	}
}
